import React, { useEffect, useState } from 'react'
import RecipeCard from '../components/RecipeCard'
import API from '../utils/API'

const MyRecipes = props => {
	const [recipes, setRecipes] = useState([])
	useEffect(() => {
		API.myRecipes()
			.then(results => {
				setRecipes(curr => [...curr, ...results])
			})
	}, [])

	// handleChange =({target}) => {
	// 	const {name, value} = target; 
	// 	this.setState({ [name]: value});
	// }

removeRecipe=(id)=>{
	const newRecipeList = this.state.recipe.filter(recipe => recipe._id !==id)
	this.setState({newRecipeList})
}

	return (
		<>
			<h1>My Recipes</h1>
			{recipes && recipes.map( recipe => <RecipeCard 
			key={recipe._id}
			recipe={recipe} 
			removeRecipe={this.removeRecipe}></RecipeCard>)}
		</>
	)
}

export default MyRecipes
