	const [directions, setDirections]= useState("");
	// const formSubmit =event=>{
		getRecipe=()=> {
		event.preventDefault();
		const allRecipe={title:title , ingrediants:ingredients, instructions: directions}
		API.viewRecipe(allRecipe)
		.then(res=>console.log(res))
		console.log(title, ingredients, directions)
		}
	componentDidMount = () => {
		this.getRecipe();

	};

	return (
		<div className="viewRecipe">
		
		<h2>My Recipe</h2>
		<div classname= {allRecipe}></div>

  
			
		 

		  
		  <a
			className="App-link"
			href="https://reactjs.org"
			target="_blank"
			rel="noopener noreferrer"
		  >
  
		  </a>
	
	  </div>
	)
}

export default View
