import React, { useEffect, useState } from 'react'
import RecipeCard from '../components/RecipeCard'
import API from '../utils/API'

const MyRecipes = props => {
	const [recipes, setRecipes] = useState([])
	useEffect(() => {
		API.myRecipes()
			.then(results => {
				setRecipes(curr => [...curr, ...results])
			})
	}, []),

	killRecipe=(id)=>{
		// var id = 2
		var newRecipeList = this.state.recipes.filter(recipe=>recipes._id !== id)
		this.setState({recipes:newRecipeList})
	  }
	// handleChange =({target}) => {
	// 	const {name, value} = target; 
	// 	this.setState({ [name]: value});
	// }



	return (
		<>
			<h1>My Recipes</h1>
			{recipes && recipes.map( recipe => <RecipeCard key={recipe._id} recipe={recipe} removeRecipe = {this.killRecipe} />)}
		</>
	)
}

export default MyRecipes
